mapper.update:
UPDATE mt_bike_station_info
SET
    bike_num=#{item.bikeNum}
  , update_time=#{item.updateTime}
WHERE bike_station_id=#{item.bikeStationId}

mapper.select:
SELECT id, bike_station_id, station_lat, station_lon , bike_num, create_time,update_time
FROM mt_bike_station_info
WHERE bike_station_id = #{bikeStationId}
